     GEN_MUXFARM_128 : if (C_DWIDTH = 128) generate
   
         signal sig_cntl_state_128    : std_logic_vector(7 downto 0) := (others => '0');
         Signal s_case_i_128          : Integer range 0 to 16 := 0;
